use indoc::indoc;


pub fn run() {
    println!("--- Day 1: Not Quite Lisp ---");
    let floor = part_a();
    println!("To what floor do the instructions take Santa?\n {}", floor);

    let position = part_b();
    println!("What is the position of the character that causes Santa to first enter the basement?\n {}", position);
}


fn part_a() -> i32 {
    let mut floor = 0;
    for c in INPUT_A.chars() {
        match c {
            '(' => floor += 1,
            ')' => floor -= 1,
            _ => ()
        }
    }
    return floor;
}

fn part_b() -> i32 {
    let mut floor = 0;
    let mut pos = 0;
    for c in INPUT_A.chars() {
        pos += 1;
        match c {
            '(' => floor += 1,
            ')' => floor -= 1,
            _ => ()
        }
        if floor == -1 {
            break;
        }
    }
    return pos;
}

